import React from 'react';

class Characters extends React.Component {
    constructor(props){
        super();
    }
    calculateIDCreationDate(creationDate) {
        return ((new Date() - new Date(creationDate)) / (1000 * 60 * 60 * 24 * 365)).toFixed(1);
    }
    render () {
        return (
            <div className="character">
                <div className="image-box">
                    <img className="character-pic" src={this.props.character.image} />
                    <div className="details-overlay">
                        <p className="name">{this.props.character.name}</p>
                        <p className="id">ID: {this.props.character.id}  - Created {this.calculateIDCreationDate(this.props.character.created)} years ago</p>
                    </div>
                </div>
                <p className="detail-item">
                    <span className="title">Status</span>
                    <span className="description">{this.props.character.status}</span>
                </p>
                <p className="detail-item">
                    <span className="title">Species</span>
                    <span className="description">{this.props.character.species}</span>
                </p>
                <p className="detail-item">
                    <span className="title">Gender</span>
                    <span className="description">{this.props.character.gender}</span>
                </p>
                <p className="detail-item">
                    <span className="title">Origin</span>
                    <span className="description">{this.props.character.origin.name}</span>
                </p>
                <p className="detail-item">
                    <span className="title">Last Location</span>
                    <span className="description">{this.props.character.location.name}</span>
                </p>
            </div>
        );
    }
}

export default Characters;
