import React from 'react';
import FilterCategory from './FilterCategory';

class SideNav extends React.Component {
    constructor(props) {
        super(props);

        this.handleGenderChange = this.handleGenderChange.bind(this);
        this.handleOriginChange = this.handleOriginChange.bind(this);
        this.handleSpeciesChange = this.handleSpeciesChange.bind(this);

        this.state = {
            filtersOpened: true
        }
        this.handleFilterAccordian = this.handleFilterAccordian.bind(this);
    }

    handleGenderChange(event) {
        this.props.filtersData.gender = event.target.value;
        this.props.getFilteredData();
    }

    handleOriginChange(envet) {
        this.props.filtersData.origin = event.target.value;
        this.props.getFilteredData();
    }

    handleSpeciesChange(event) {
        this.props.filtersData.species = event.target.value;
        this.props.getFilteredData();
    }

    handleFilterAccordian(event) {
        const context = this;
        this.setState({ filtersOpened: !context.state.filtersOpened });
    }

    render () {
        return (
            <aside className={this.state.filtersOpened ? 'sidebar clearfix' : 'sidebar clearfix no-filters'}>
                <div className="heading-box">
                    <h2 className="sub-heading">Filters</h2>
                    <button className="accordion-btn" onClick={this.handleFiltersAccordion}></button>
                </div>
            </aside>
        );
    }
}

export default SideNav;
